2\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for (int k=0; k&lt;i; k++){</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; B[k] = A[k];</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; }</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; String xyz = \\"abc\\";</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; String abc = \\"xyz\\";</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; System.out.println(xyz.compareTo(abc));</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; }</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">}</font></div>', 'parafron8295', 15, '2012-02-08 19:43:34'),
(41, 3, 'C# File input example', 'c-file-input-example', 0, '<div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">public static string getFileAsString(string fileName) {</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;StreamReader sReader = null;</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;string contents = null;</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;try {</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; FileStream fileStream = new FileStream(fileName, FileMode.Open, FileAccess.Read);</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; sReader = new StreamReader(fileStream);</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; contents = sReader.ReadToEnd();</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;} finally {</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp;if(sReader != null) {</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sReader.Close();</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp; &nbsp; }</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;}</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">&nbsp; &nbsp;return contents;</font></div><div><font face=\\"\\''Courier New\\''\\" size=\\"2\\">}</font></div>', 'parafron8295', 3, '2012-02-09 13:15:01'),
(43, 3, 'Event Handlers', 'event-handlers', 0, '<pre style=\\"padding-top: 5px; padding-right: 5px; padding-bottom: 5px; padding-left: 5px; margin-top: 0px; margin-bottom: 0px; font-family: Consolas, Courier, monospace; word-break: break-all; word-wrap: break-word; overflow-x: auto; overflow-y: auto; text-align: left; \\">using System;\r\nnamespace MyCollections \r\n{\r\n   using System.Collections;\r\n\r\n   // A delegate type for hooking up change notifications.\r\n   public delegate void ChangedEventHandler(object sender, EventArgs e);\r\n\r\n   // A class that works just like ArrayList, but sends event\r\n   // notifications whenever the list changes.\r\n   public class ListWithChangedEvent: ArrayList \r\n   {\r\n      // An event that clients can use to be notified whenever the\r\n      // elements of the list change.\r\n      public event ChangedEventHandler Changed;\r\n\r\n      // Invoke the Changed event; called whenever list changes\r\n      protected virtual void OnChanged(EventArgs e) \r\n      {\r\n         if (Changed != null)\r\n            Changed(this, e);\r\n      }\r\n\r\n      // Override some of the methods that can change the list;\r\n      // invoke event after each\r\n      public override int Add(object value) \r\n      {\r\n         int i = base.Add(value);\r\n         OnChanged(EventArgs.Empty);\r\n         return i;\r\n      }\r\n\r\n      public override void Clear() \r\n      {\r\n         base.Clear();\r\n         OnChanged(EventArgs.Empty);\r\n      }\r\n\r\n      public override object this[int index] \r\n      {\r\n         set \r\n         {\r\n            base[index] = value;\r\n            OnChanged(EventArgs.Empty);\r\n         }\r\n      }\r\n   }\r\n}</pre>', 'ragnarok0211', 6, '2012-02-09 16:05:39'),
